import { LANGUAGES, TLLanguage, useEditor } from '@tldraw/editor'

/** @internal */
export function useLanguages() {
	const editor = useEditor()
	return {
		languages: LANGUAGES as readonly TLLanguage[],
		currentLanguage: editor.locale,
	}
}
